    const [user, setUser] = useState({
        name: 'Ardi',
        age: '22',
        posts: ['My First Post']
    });

    const increment = () => {
        setCount(count + 1);
    }
    return (
        <div className="app">
            <button onClick={increment}>Increment</button>
            <h1>{count}</h1>
        </div>
    );
}

export default App;
